import React, { useState } from "react";
import { runFlow } from "../services/backend";
import { Spinner } from "../Components/Spinner/Spinner";
import { Input } from "../Components/Input"; // ðŸ‘ˆ importamos tu componente Input
import "./Home.css";

export function Home() {
  const [answer, setAnswer] = useState("");
  const [loading, setLoading] = useState(false);

  const handleAsk = async (question) => {
    if (!question.trim()) {
      alert("Please enter a question.");
      return;
    }
    setLoading(true);
    setAnswer("");

    try {
      const responseHtml = await runFlow(question);
      setAnswer(responseHtml); // ðŸ”¹ el flujo devuelve HTML
    } catch (err) {
      console.error("Error running flow:", err.message);
      setAnswer("There was an error getting the response.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="page1-container">
      <h4>Ask me anything about fruits</h4>

      {/* ðŸ‘‡ Usamos el componente Input */}
      <Input onSubmit={handleAsk} />

      <div className="page1-block">
        {loading ? (
          <Spinner text="Thinking..." />
        ) : (
          <div
            className="page1-line"
            dangerouslySetInnerHTML={{ __html: answer }}
          />
        )}
      </div>
    </div>
  );
}
